import React, { useState } from 'react';

function App() {
  const [birthDate, setBirthDate] = useState('');
  const [retirementData, setRetirementData] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:5000/api/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ birthDate, personType: 'male' }),
    });

    const data = await response.json();
    setRetirementData(data);
  };
  return (
    <div className="App">
      <h1>法定退休年龄计算器 - 男职工</h1>
      <form onSubmit={handleSubmit}>
        <label>
          出生年月：
          <input
            type="month"
            value={birthDate}
            onChange={(e) => setBirthDate(e.target.value)}
            required
          />
        </label>
        <button type="submit">计算退休年龄</button>
      </form>

      {retirementData && (
        <div>
          <p>您的改革后法定退休年龄为：{retirementData.retirementAgeYears}岁 {retirementData.retirementAgeMonths}个月</p>
          <p>您的改革后退休时间为：{retirementData.retirementDate}</p>
          <p>您的延迟月数为：{retirementData.delayMonths}个月</p>
        </div>
      )}
    </div>
  );
}

export default App;
