import React, { useState } from 'react';

function App() {
  const [birthDate, setBirthDate] = useState('');
  const [personType, setPersonType] = useState('male');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:3000/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ birthDate, personType }),
    });

    const data = await response.json();
    setResult(data);
  };

  return (
    <div className="App">
      <h1>法定退休年龄计算器</h1>
      <form onSubmit={handleSubmit}>
        <label>
          出生年月：
          <input
            type="month"
            value={birthDate}
            onChange={(e) => setBirthDate(e.target.value)}
            required
          />
        </label>
        <label>
          人员类型：
          <select value={personType} onChange={(e) => setPersonType(e.target.value)}>
            <option value="male">男职工</option>
            <option value="female55">原法定退休年龄55周岁女职工</option>
            <option value="female50">原法定退休年龄50周岁女职工</option>
          </select>
        </label>
        <button type="submit">计算退休年龄</button>
      </form>

      {result && (
        <div>
          <p>您的改革后法定退休年龄为：{result.retirementAge}岁</p>
          <p>您的改革后退休时间为：{result.retirementDate}</p>
          <p>您的延迟月数为：{result.delayMonths}个月</p> 
        </div>
      )}
    </div>
  );
}

export default App;
